<span class="token punctuation">}</span>

<span class="token keyword">const</span> xiaoman <span class="token operator">=</span> <span class="token punctuation">(</span>man<span class="token operator">:</span>Pople <span class="token operator">&amp;</span> Man<span class="token punctuation">)</span><span class="token operator">:</span><span class="token keyword">void</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
  <span class="token builtin">console</span><span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>man<span class="token punctuation">)</span>
<span class="token punctuation">}</span>

<span class="token function">xiaoman</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
