<span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span>resultType <span class="token operator">===</span> <span class="token string">"as-string"</span><span class="token punctuation">)</span>
        <span class="token keyword">return</span> n1<span class="token punctuation">.</span><span class="token function">toString</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">+</span> n2<span class="token punctuation">.</span><span class="token function">toString</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span><span class="token keyword">typeof</span> n1 <span class="token operator">===</span> <span class="token string">"string"</span> <span class="token operator">||</span> <span class="token keyword">typeof</span> n2 <span class="token operator">===</span> <span class="token string">"string"</span><span class="token punctuation">)</span>
        <span class="token keyword">return</span> n1<span class="token punctuation">.</span><span class="token function">toString</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">+</span> n2<span class="token punctuation">.</span><span class="token function">toString</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token keyword">else</span>
        <span class="token keyword">return</span> n1 <span class="token operator">+</span> n2
<span class="token punctuation">}</span>

<span class="token keyword">let</span> mergeNumber <span class="token operator">=</span> <span class="token function">merge</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> <span class="token number">5</span><span class="token punctuation">,</span> <span class="token string">"as-number"</span><span class="token punctuation">)</span>
<span class="token keyword">let</span> mergeNumber <span class="token operator">=</span> <span class="token function">merge</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> <span class="token number">5</span><span class="token punctuation">,</span> <span class="token string">"as-string"</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h4 id="枚举类型-enum" tabindex="-1"><a class="header-anchor" href="#枚举类型-enum" aria-hidden="true">#</a> 枚举类型(Enum)</h4>
<ul>
<li>枚举类型真正的数据类型是数字 (与 C++ 类似, 从 0 开始计算)</li>
</ul>
<div class="language-typescript line-numbers-mode" data-ext="ts"><pre v-pre class="language-typescript"><code><span class="token comment">// 枚举类型定义</span>
<span class="token keyword">enum</span> Color <span class="token punctuation">{</span>
    red<span class="token punctuation">,</span>
    green<span class="token punctuation">,</span>
    blue
<span class="token punctuation">}</span>
<span class="token comment">// 枚举类型使用</span>
<span class="token keyword">let</span> color <span class="token operator">=</span> Color<span class="token punctuation">.</span>blue
<span class="token builtin">console</span><span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>color<span class="token punctuation">)</span> <span class="token comment">// 2</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><div class="language-typescript line-numbers-mode" data-ext="ts"><pre v-pre class="language-typescript"><code><span class="token comment">// 可以自定义枚举类型顺序</span>
<span class="token keyword">enum</span> Color2 <span class="token punctuation">{</span>
    red <span class="token operator">=</span> <span class="token number">5</span><span class="token punctuation">,</span>
    green <span class="token operator">=</span> <span class="token number">10</span><span class="token punctuation">,</span>
    blue <span class="token operator">=</span> <span class="token number">1</span>
<span class="token punctuation">}</span>
<span class="token builtin">console</span><span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>Color2<span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><img src="@source/../../../project/Lin-Note/public/image-20230211184857316-1676112565228-12.png" alt="image-20230211184857316" style="zoom:120%;" />	
<div class="language-typescript line-numbers-mode" data-ext="ts"><pre v-pre class="language-typescript"><code><span class="token comment">// 枚举类型的元素也可以定义为其他类型</span>
<span class="token keyword">enum</span> Color3 <span class="token punctuation">{</span>
    red <span class="token operator">=</span> <span class="token string">"red"</span><span class="token punctuation">,</span>
    green <span class="token operator">=</span> <span class="token string">"green"</span><span class="token punctuation">,</span>
    blue <span class="token operator">=</span> <span class="token number">1</span>
<span class="token punctuation">}</span>
<span class="token keyword">let</span> color3 <span class="token operator">=</span> Color3<span class="token punctuation">.</span>green
<span class="token builtin">console</span><span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>color3<span class="token punctuation">)</span> <span class="token comment">// green</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>数字枚举</strong></p>
<p>使用枚举之前, 我们判断根据参数不同, 执行不同的方法</p>
<div class="language-typescript line-numbers-mode" data-ext="ts"><pre v-pre class="language-typescript"><code><span class="token comment">// 之前的写法</span>
<span class="token keyword">const</span> <span class="token function-variable function">fn</span> <span class="token operator">=</span> <span class="token punctuation">(</span>type<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span>type <span class="token operator">===</span> <span class="token string">'red'</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token keyword">return</span> <span class="token number">0</span>
    <span class="token punctuation">}</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span>type <span class="token operator">===</span> <span class="token string">'green'</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token keyword">return</span> <span class="token number">1</span>
    <span class="token punctuation">}</span>
    <span class="token keyword">if</span> <span class="token punctuation">(</span>type <span class="token operator">===</span> <span class="token string">'blue'</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token keyword">return</span> <span class="token number">2</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token comment">// 升级一点的写法</span>
<span class="token keyword">let</span> obj <span class="token operator">=</span> <span class="token punctuation">{</span>
    red<span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span>
    green<span class="token operator">:</span> <span class="token number">1</span><span class="token punctuation">,</span>
    blue<span class="token operator">:</span> <span class="token number">2</span>
<span class="token punctuation">}</span>

<span class="token comment">// 使用枚举</span>
<span class="token keyword">enum</span> Color <span class="token punctuation">{</span>
    red<span class="token punctuation">,</span> <span class="token comment">// 0</span>
    green<span class="token punctuation">,</span> <span class="token comment">// 1</span>
    blue <span class="token comment">// 2</span>
<span class="token punctuation">}</span>

<span class="token comment">// 增长枚举</span>
<span class="token keyword">enum</span> ColorA <span class="token punctuation">{</span>
    red <span class="token operator">=</span> <span class="token number">1</span><span class="token punctuation">,</span> <span class="token comment">// 1</span>
    green<span class="token punctuation">,</span> <span class="token comment">// 2</span>
    blue <span class="token comment">// 3</span>
<span class="token punctuation">}</span>

<span class="token comment">// 自定义枚举</span>
<span class="token keyword">enum</span> ColorB <span class="token punctuation">{</span>
