<span class="token builtin">console</span><span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>ColorD<span class="token punctuation">.</span>yes<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// 1</span>
<span class="token builtin">console</span><span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>ColorD<span class="token punctuation">.</span>no<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// no</span>
