<span class="token comment">// class 类项 implements 接口 {  }</span>
<span class="token keyword">export</span> <span class="token keyword">class</span> <span class="token class-name">Point</span> <span class="token keyword">implements</span> <span class="token class-name">IPoint</span> <span class="token punctuation">{</span>
