    <span class="token punctuation">}</span>
    
    <span class="token comment">// 实现成员方法</span>
    <span class="token function-variable function">drawPoint</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
        <span class="token comment">// 点位置</span>
        <span class="token builtin">console</span><span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">"x: "</span><span class="token punctuation">,</span> <span class="token keyword">this</span><span class="token punctuation">.</span>_x_<span class="token punctuation">,</span> <span class="token string">"y: "</span><span class="token punctuation">,</span> <span class="token keyword">this</span><span class="token punctuation">.</span>_y_<span class="token punctuation">)</span>
