        <span class="token comment">// 两点之间平方和</span>
        <span class="token keyword">return</span> Math<span class="token punctuation">.</span><span class="token function">pow</span><span class="token punctuation">(</span>p<span class="token punctuation">.</span><span class="token constant">X</span> <span class="token operator">-</span> <span class="token keyword">this</span><span class="token punctuation">.</span>_x_<span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">)</span> <span class="token operator">+</span> Math<span class="token punctuation">.</span><span class="token function">pow</span><span class="token punctuation">(</span>p<span class="token punctuation">.</span><span class="token constant">Y</span> <span class="token operator">-</span> <span class="token keyword">this</span><span class="token punctuation">.</span>_y_<span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span>
