    <span class="token function">listen</span><span class="token punctuation">(</span>port<span class="token operator">:</span><span class="token builtin">number</span><span class="token punctuation">,</span>cb<span class="token operator">?</span><span class="token operator">:</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token operator">=></span><span class="token keyword">void</span><span class="token punctuation">)</span>
  <span class="token punctuation">}</span>

  <span class="token keyword">interface</span> <span class="token class-name">Express</span> <span class="token punctuation">{</span>
    <span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token operator">:</span> App
    <span class="token function">Router</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token operator">:</span>Router
